import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ProductCard from "../components/ProductCard";
import { products } from "../data/products";

export default function Products() {
  const { category } = useParams();
  const navigate = useNavigate();

  const [selectedCategory, setSelectedCategory] = useState(category || null);

  // ================= Categories =================
  const categories = [
    {
      name: "Tote Bags",
      image:
        "https://cdn.pixabay.com/photo/2025/10/30/20/51/tote-bag-9927928_1280.jpg",
    },
    {
      name: "Clutches",
      image:
        "https://cdn.pixabay.com/photo/2016/11/23/18/12/bag-1854148_1280.jpg",
    },
    {
      name: "Shoulder Bag",
      image:
        "https://cdn.pixabay.com/photo/2017/07/04/18/27/handbags-white-2472100_1280.jpg",
    },
    {
      name: "Cross Body",
      image:
        "https://cdn.pixabay.com/photo/2015/11/20/03/53/package-1052370_1280.jpg",
    },
    {
      name: "Bridal Purse",
      image:
        "https://stylo.pk/cdn/shop/files/P2410815_1_9db901f9-e2ef-4488-b61e-dd4213620e23.jpg?v=1757337273&width=600",
    },
    {
      name: "Mini Bags",
      image:
        "https://static.vecteezy.com/system/resources/thumbnails/031/321/060/small_2x/beautiful-elegance-and-luxury-fashion-women-handbag-or-purse-a-glamour-female-leather-hand-made-bag-concept-by-ai-generated-free-photo.jpg",
    },
  ];

  const repeatProducts = (arr) => {
    if (arr.length >= 6) return arr;

    let repeated = [...arr];
    while (repeated.length < 6) {
      repeated = [...repeated, ...arr];
    }
    return repeated.slice(0, 6);
  };

  const filteredProducts = selectedCategory
    ? repeatProducts(products.filter((p) => p.category === selectedCategory))
    : [];

  useEffect(() => {
    if (selectedCategory) {
      navigate(`/products/${selectedCategory}`, { replace: true });
    }
  }, [selectedCategory, navigate]);

  return (
    <section className="py-20 bg-warm-ivory">
      <div className="max-w-7xl mx-auto px-4">

        {/* ================= Categories View ================= */}
        {!selectedCategory && (
          <>
            <h1 className="text-4xl md:text-5xl font-bold text-center mb-4 text-[#5a4633]">
              Explore Our <span className="text-[#c8a97e]">Luxury Collection</span>
            </h1>

            <p className="text-center text-[#5a4633] max-w-2xl mx-auto mb-14 text-lg">
              Discover premium handcrafted purses designed for elegance,
              comfort, and everyday luxury.
            </p>

            {/*  Mobile + Desktop  3 Boxes Per Row */}
            <div className="grid grid-cols-3 gap-4 md:gap-6">
              {categories.map((cat) => (
                <div
                  key={cat.name}
                  onClick={() => setSelectedCategory(cat.name)}
                  className="relative group cursor-pointer overflow-hidden rounded-xl bg-white shadow-md hover:shadow-2xl transition"
                >
                  <img
                    src={cat.image}
                    alt={cat.name}
                    className="w-full h-36 md:h-56 object-cover transition-transform duration-500 group-hover:scale-105"
                  />

                  <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/20 to-transparent flex items-end justify-center p-3 md:p-5">
                    <p className="text-white text-xs md:text-xl font-semibold tracking-wide group-hover:text-[#c8a97e] transition">
                      {cat.name}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {/* ================= Products View ================= */}
        {selectedCategory && (
          <>
            <button
              onClick={() => {
                setSelectedCategory(null);
                navigate("/");
              }}
              className="mb-10 px-6 py-2 rounded-full bg-[#5a4633] text-white shadow hover:bg-[#3f3124] transition"
            >
               Back to Categories
            </button>

            <h2 className="text-3xl font-semibold mb-10 text-[#5a4633]">
              Showing:{" "}
              <span className="text-[#c8a97e]">{selectedCategory}</span>
            </h2>

            <div className="grid grid-cols-3 gap-6">
              {filteredProducts.map((product, i) => (
                <ProductCard key={product.id + "-" + i} product={product} />
              ))}
            </div>
          </>
        )}

      </div>
    </section>
  );
}
